mapper.select:
select media_id from media m, media_sale ms 
				where m.sale_id = ms.sale_id
				and  ms.shelf_status =1 and media_id is not null  and  ms.sale_id is not null limit #{limit_offset},#{limit_count}

mapper.insert:
insert into media_statistics(media_id,media_title,media_cover,media_category_ids,sale_id,sale_count,statistics_day,media_creation_date,media_chapter_change_date) select m.media_id,title,cover_pic, group_concat(DISTINCT path) as media_category_ids,m.sale_id,0 as sale_count, CURDATE() as statistics_day,creation_date,last_pull_chapter_date from media_book_catetory mbc,media_catetory mc,media m where m.media_id in
#{item}
and mbc.catetory_id = mc.catetory_id and m.media_id= mbc.media_id group by mbc.media_id

mapper.update:
update media_statistics ms INNER JOIN ( select media_id,count(DISTINCT mom.order_no) as sale_count from media_order_main mom left join media_order_detail modd on mom.order_no = modd.order_no left join media on media.sale_id = modd.sale_info_id where mom.order_type =1 and mom.creation_date BETWEEN timestamp(adddate(CURDATE(),-1)) and timestamp(CURDATE()) and media_id in
#{item}
group by media_id )as temp on ms.media_id=temp.media_id set ms.sale_count = temp.sale_count where statistics_day = CURDATE()

UPDATE media_statistics ms INNER JOIN ( SELECT mediaId, sum(mub.times) AS download_count FROM media_user_behavior mub WHERE mub.behavior_type = 'download' AND mub.create_time BETWEEN timestamp(adddate(CURDATE(),-1)) and timestamp(CURDATE()) AND mub.mediaId IN
#{item}
GROUP BY mub.mediaId ) AS temp ON ms.media_id = temp.mediaId SET ms.download_count = temp.download_count WHERE statistics_day = CURDATE()

UPDATE media_statistics ms INNER JOIN ( SELECT mediaId, sum(mub.times) AS search_count FROM media_user_behavior mub WHERE mub.behavior_type = 'search' AND mub.create_time BETWEEN timestamp(adddate(CURDATE(),-1)) and timestamp(CURDATE()) AND mub.mediaId IN
#{item}
GROUP BY mub.mediaId ) AS temp ON ms.media_id = temp.mediaId SET ms.search_count = temp.search_count WHERE statistics_day = CURDATE()

UPDATE media_statistics ms INNER JOIN ( SELECT mediaId, sum(mub.times) AS play_count FROM media_user_behavior mub WHERE mub.behavior_type = 'play' AND mub.create_time BETWEEN timestamp(adddate(CURDATE(),-1)) and timestamp(CURDATE()) AND mub.mediaId IN
#{item}
GROUP BY mub.mediaId ) AS temp ON ms.media_id = temp.mediaId SET ms.play_count = temp.play_count WHERE statistics_day = CURDATE()

update media_statistics ms INNER JOIN ( select media_id,sum(main_gold_used) as reward_gold ,mcc.creation_date from media_consumer_consume mcc,media_consumer_consume_detail mccd where mcc.consume_tpye =4 and mcc.creation_date BETWEEN timestamp(adddate(CURDATE(),-2)) and timestamp(CURDATE()) and media_id in
#{item}
and media_id is not NULL and mcc.consumer_consume_id = mccd.consumer_consume_id group by media_id) as temp on ms.media_id=temp.media_id set ms.rewards_count = temp.reward_gold where ms.statistics_day=CURDATE()

mapper.select:
SELECT mbc.media_id mediaId, GROUP_CONCAT(mc.`name` SEPARATOR ',') AS categorys, GROUP_CONCAT(mc.`code` SEPARATOR ',') AS categoryIds FROM media_book_catetory mbc, media_catetory mc WHERE mbc.catetory_id = mc.catetory_id AND mbc.media_id in
${item}
GROUP BY mbc.media_id
